sys_script_include.script:
var StdChangeUtils = Class.create();
StdChangeUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	APP_SCOPE: 'f494c8990ff536406da728b8b1050e64', //K17
	TABLE_NAME_PROPERTIES: 'std_change_properties',
	helper: new global.StdChangeUtils_Helper(),

	initialize: function(request, responseXML, gc) {
		//StdChangeUtilsSNC.prototype.initialize.call(this, request, responseXML, gc);
	},

	/***************************
	 *
	 *Add customer changes below
	 *
	 ****************************/
	_getPropertyValue: function(propertyName) {
		if (!this._propGr) {
			var gr = new GlideRecord(this.TABLE_NAME_PROPERTIES);
			// Using number instead of sysid since if ever this row
			// gets deleted then a row with same number can be
			// recreated. But row with same sysid is impossible to create.
			//gr.addQuery('internal_name', this.PROP_ROW_KEY);
			gr.addQuery('sys_scope',this.APP_SCOPE);
			gr.query();
			if (!gr.next()) {
				this.log.logErr('Property row not found!');
				return;
			}
			this._propGr = gr;
		}
		var returnVal = this._propGr.getValue(propertyName);
		if (returnVal == null) {
			returnVal = '';
		}
		return this.helper.j2js(returnVal);
	},
	getValue: function(property) {
		return this._getPropertyValue(property);
	},

    type: 'StdChangeUtils'
});
